export const favorite = (state = [], action) => {
  switch (action.type) {
    case "ADD_FAVORITE":
      return [...state, action.makeup];
    case "REMOVE_FAVORITE":
      let newState = [...state];
      newState.find(action.makeup.id).splice(i, 1)
    default: 
      return state;
  };
};
