export const favorite = (state = [], action) => {
  switch (action.type) {
    case "ADD_FAVORITE":
      return [...state, action.makeup];
    case "REMOVE_FAVORITE":
      let newState = [...state];
      let foundID = newState.find(action.makeup.id)

    default: 
      return state;
  };
};
